mapper.select:
select * from (select party.*, row_number() over(order by
seq desc) as rnum
from party where place_id = #{place_id}) where rnum
between #{start}
and #{end}

select * from (select party.*, row_number() over(order by seq desc) as rnum from party
parent_address like '%${address}%' or gender = '${gender}' or age like
'%${aa}%'
or drinking = ${drinking} or title like '%${title}%' or writer like '%${writer}%' or content like '%${content}%' or title like '%${both}%' or content like '%${both}%'
)where rnum between ${start} and ${end}

select * from (select party.*, row_number() over(order by
seq desc) rnum from party where seq in (select chatSeq_parent from partylist where participant = #{nickname})) where rnum between
#{start} and #{end}
